mapper.insert:

Insert into ASSESSMENT 
(
  ORGNZT_ID
, AI_CD
, ASSESS_FILE_ID
, ASSESS_USR_ID
, FRST_REGIST_PNTTM
, FRST_REGISTER_ID
) values (
  #{ORGNZT_ID}
, CONCAT('CAI', #{AI_CD})
, #{FILE_ID}
, #{USER_ID}
, SYSDATE()
, #{USER_ID}
)
ON DUPLICATE KEY UPDATE 
  ASSESS_FILE_ID = #{FILE_ID}
, ASSESS_USR_ID = #{USER_ID}
, LAST_UPDT_PNTTM = SYSDATE()
, LAST_UPDUSR_ID = #{USER_ID}


Insert into ASSESSMENT 
(
  ORGNZT_ID
, AI_CD
, ASSESS_FILE_ID
, FRST_REGIST_PNTTM
, FRST_REGISTER_ID
, RATING_SCORE
, RATING_USR_ID
) values (
  #{ORGNZT_ID}
, #{AI_CD}
, ''
, SYSDATE()
, #{USER_ID}
, #{RATING_SCORE}
, #{USER_ID}
)
ON DUPLICATE KEY UPDATE 
  RATING_SCORE = #{RATING_SCORE}
, RATING_USR_ID = #{USER_ID}
, LAST_UPDT_PNTTM = SYSDATE()
, LAST_UPDUSR_ID = #{USER_ID}


Insert into ASSESS_ATCH_FILE 
(
  ORGNZT_ID
, AI_CD
, SEQ_NO
, ATCH_FILE_ID
, FRST_REGIST_PNTTM
, FRST_REGISTER_ID
) values (
  #{ORGNZT_ID}
, CONCAT('CAI', #{AI_CD})
, (SELECT SEQ_NO FROM(SELECT IFNULL(MAX(SEQ_NO), 0)+1 SEQ_NO 
                        FROM ASSESS_ATCH_FILE 
                       WHERE ORGNZT_ID = #{ORGNZT_ID}
                         AND AI_CD = CONCAT('CAI', #{AI_CD}))TMP)
, #{FILE_ID}
, SYSDATE()
, #{USER_ID}
)
